mapper.update:
update review_user
set user_name = #{userName},sex = #{sex} ,group_id = #{groupId},real_name = #{realName},password = #{password},mobile_phone = #{mobilePhone}
where user_id = #{userId}

mapper.select:
SELECT class_id classId, q.user_id userId, q.result_id resultId, u.user_name userName, u.real_name realName, u.sex sex, u.age age, u.mobile_phone mobilePhone, u.group_id groupId, u.extra extra, q.question_num questionNum, concat( q.question_num, '.', q.content ) AS content, q.sel_code selCode, q.select_grade selectGrade, q.right_answer rightAnswer, DATE_FORMAT( q.`create_time`, '%Y-%m-%e %H:%i:%S' ) AS createTime FROM review_question_answer q,review_user u WHERE q.user_id = u.user_id AND q.group_id = #{groupId}
AND q.project_id = #{projectId}
AND q.create_time >= #{startTime}
AND q.create_time <= #{endTime}
